#include <iostream>
#include <string>
#include "bindings/cpp/WFAligner.hpp"
#include <vector>
using namespace std;
using namespace wfa;



int process_cigar( string cigar, string& pattern , string& res_patter, vector<int>& res_text);



int main(int argc,char* argv[]) {
  // Patter & Text
  // string pattern = "  G G G A G G G C G C T T A C C A C T T T G T G A T T C A T G A C T G G G G T G A A G T C G T A A C A A G G T A A C C G T A G G G G A A C C T G C G G T T G G A T C A C C T C C T T A";
  // string text    = "C G G G G G G G A G C T T A C A A C G T T G C G A T T C A T G C C C G T G G T G A   G A C G T A A C A G A C T A A C C G C A G G G G G A C C T G C G G T T G G T T T T C C G A G T T A";
  //                 //I M M M X M M M X M M M M M M X M M X M M M X M M M M M M M M X M X M X M M M M M D M X M M M M M M M X X X M M M M M M X M M M M M X M M M M M M M M M M M M X M X X M M X X X M M M
                     
  string text        = "";
  string pattern1    = "";
  //string pattern2    = "GATCACAGGTCCTATTAAGGGGGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGTCTGGGGGGTGTGTATCACCCACGCGATAGCATTGCGAGACGCTGGAGCCGGAGCACCCTATGTCGCAGTATCTGTCTTTGATTCCTGCCTCATCCTATTATTTATCGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGT";
                    // TEXT:     GATCACAGGTCTATCACCCTAGTAAGGGGGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGTCTGGGGGGTGTGTATCACCCACGCGATAGCATTGCGAGACGCTGGAGCCGGAGCACCCTATGTCGCAGTATCTGTCTTTGATTCCTGCCTC---------------GCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGTATCCTATTATTTATC
                    // PATTERN1: GTTCACAGGTC-------CTATTAAGGGGGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGTCTGGGGGGTGTGTATCACCCACGCGATAGCATTGCGAGACGCTGGAGCCGGAGCACCCTATGTCGCAGTATCTGTCTTTGATTCCTGCCTCATCCTATTATTTATCGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGT---------------
                    // PATTERN2: GATCACAGGTC-------CTATTAAGGGGGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGTCTGGGGGGTGTGTATCACCCACGCGATAGCATTGCGAGACGCTGGAGCCGGAGCACCCTATGTCGCAGTATCTGTCTTTGATTCCTGCCTCATCCTATTATTTATCGCCACTCACGGGAGCTCTCCATGCATTTGGTATTTTCGT---------------
                    
  // D 对text插空， I 对pattern插空， M 匹配， X 不匹配
  // Create a WFAligner
  // // mismatch gapOpening gapExtension
  // 
  WFAlignerGapAffine aligner(4,6,2,WFAligner::Alignment,WFAligner::MemoryHigh);
  // Align
  aligner.alignEnd2End(pattern1,text);
  cout << "WFA-Alignment returns score " << aligner.getAlignmentScore() << endl;
  string res_patter1 = "", res_patter2 = "";
  vector<int> res_text1;
  
  process_cigar(aligner.getAlignment(), pattern1, res_patter1, res_text1);
  vector<int> res_text2;
  //process_cigar(aligner.getAlignment(), pattern2, res_patter2, res_text2);
  int le = 0, ri = 0;

  vector<int> final_res(max(res_text1.size(), res_text2.size()) , 0);
  int op = 0;
  while(le < res_text1.size() && ri < res_text2.size()){
    final_res[op++] = res_text1[le++] & res_text2[ri++];
  }
  while(le < res_text1.size()){
    final_res[op++] = res_text1[le++];
  }
  while(ri < res_text2.size()){
    final_res[op++] = res_text2[ri++];
  }
  op = 0;
  string final_text;
  for(int i = 0 ; i < final_res.size(); i++){
    if(final_res[i] == 1){
      final_text += '-';
    }else{
      final_text += text[op++];
    }
  }
  // Print CIGAR
  string cigar = aligner.getAlignment();
  cout << "PATTERN1: " << res_patter1  << endl;
  cout << "PATTERN2: " << res_patter2  << endl;
  cout << "TEXT: " << final_text  << endl;
  cout << "CIGAR: " << cigar  << endl;

  return 0;
}

int process_cigar(string cigar, string& pattern , string& res_patter, vector<int>& res_text){
    int count_X = 0 , count_I = 0, count_M = 0 , count_D = 0;
    int op_pa = 0 , op_te = 0;
    for(int i = 0 ; i < cigar.size(); i++){
        char ch = cigar[i];
        switch(ch){
            case 'X' :{
                res_patter += pattern[op_pa++];
                res_text.push_back(0);
                count_X++;
                break;
            }
            case 'I' :{
                res_patter += '-';
                count_I++;
                res_text.push_back(0);
                break;
            }
            case 'M' :{
                res_patter += pattern[op_pa++];
                res_text.push_back(0);
                count_M++;
                break;
            }
            case 'D' :{
                res_patter += pattern[op_pa++];
                res_text.push_back(1);
                count_D++;
                break;
            }default:{
                 cout << "other char : " << ch <<  endl;
                break;
            }
        }
    }
    return count_X + count_I + count_D;
}
